import React from "react";
import {connect} from "react-redux";
import {Field, reduxForm} from 'redux-form'
import style from '../common/FormControls/FormControls.module.css'
import {Input} from "../common/FormControls/FormControls";
import {maxLength10, required} from "../../utils/validator/validator";
import {login} from "../../redux/auth-reducer";


const LoginForm = (props) => {

    return (

        <form onSubmit={props.handleSubmit}>


            <div>
                <Field  placeholder={'Email'} name={'email'} validate={[required]} component={Input}/>
            </div>
            <div>
                < Field validate={[required]} placeholder={'passwоrd'} type={'password'} name={'password'} component={Input}/>
            </div>
            <div className={style.formSummeryError}>{props.error}</div>   
            <div>
                <Field   type={'checkbox'} name={'rememberMe'} component={Input}/>
            </div>


            <button>Login</button>
        </form>
    )

}


const ReduxLoginForm = reduxForm({
    // a unique name for the form
    form: 'syncValidation',

})(LoginForm)

let Login = (props) => {

    let loginSubmit = (formData) => {

        props.login(formData.email, formData.password, formData.rememberMe)
        console.log(formData)

    }

    return <div>
        <h1>Login</h1>
        <ReduxLoginForm onSubmit={loginSubmit}/>
    </div>


}


// You have to connect() to any reducers that you wish to connect to yourself

export default connect(null, {login})(Login)
