import React from "react";

import {connect} from "react-redux";

import {
    follow,
    setCurrentPage,
    setIsFetching,
    setTotalUsersCount,
    setUsers,
    unfollow
} from "../../redux/users-reducer";

import * as axios from "axios";
import Users from "./Users";
import Preloader from "../common/preloader/Preloader";
import {getUsers} from "../../api/api";

class UsersContainer extends React.Component {

    constructor(props) {
        super(props);
        console.log(props)
    }

    componentDidMount() {
        this.props.setIsFetching(true)
        getUsers(this.props.currentPage, this.props.pageSize).then(data => {

            this.props.setIsFetching(false)
            this.props.setUsers(data.items)
            this.props.setTotalUsersCount(data.totalCount)
        })

        console.log(this.props)
    }

    loadUsers = (page, flag) => {
        page = this.props.currentPage;
        page += 1;
        this.props.setCurrentPage(page);
        this.props.setIsFetching(true)
        console.log(page);
        getUsers(page, this.props.pageSize).then(response => {
            this.props.setIsFetching(false)
            this.props.setUsers(data.items, flag);
        })

    }

    setCurrentPage2 = (page) => {

        this.props.setCurrentPage(page);
        this.props.setIsFetching(true)
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${page}&count=${this.props.pageSize}`, {withCredentials:true}).then(response => {
            this.props.setIsFetching(false)
            this.props.setUsers(response.data.items)
        })


    }
    isFollowed = (page) => {

        this.props.setCurrentPage(page);
        this.props.setIsFetching(true)
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${page}&count=${this.props.pageSize}`).then(response => {
            this.props.setIsFetching(false)
            this.props.setUsers(response.data.items)
        })


    }
    isFollow = (page) => {

        this.props.setCurrentPage(page);
        this.props.setIsFetching(true)
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${page}&count=${this.props.pageSize}`).then(response => {
            this.props.setIsFetching(false)
            this.props.setUsers(response.data.items)
        })


    }

    render() {
        return <>
            {this.props.isFetching ? <Preloader/>: null}
            <Users totalUsersCount={this.props.totalUsersCount}
                   pageSize={this.props.pageSize}
                   users={this.props.users}
                   setCurrentPage={this.setCurrentPage2}
                   unfollow={this.props.unfollow}
                   follow={this.props.follow}
                   loadUsers={this.loadUsers}
                   currentPage={this.props.currentPage}/>
        </>
    }
}

let mapStateToProps = (state) => {

    return {
        users: state.usersPage.users,
        pageSize: state.usersPage.pageSize,
        totalUsersCount: state.usersPage.totalUsersCount,
        currentPage: state.usersPage.currentPage,
        isFetching: state.usersPage.isFetching,
    };
};


export default connect(mapStateToProps,{
    follow,
    unfollow,
    setUsers,
    setTotalUsersCount,
    setCurrentPage,
    setIsFetching
})(UsersContainer)
