import React, {useState} from "react";

import {NavLink} from "react-router-dom";
import s from "./Users.module.css"
import Pagination from "react-js-pagination";
// require("bootstrap/less/bootstrap.less");
let page = 1
let Users = (props) => {
    console.log(props)

    let flag = true;
    let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize);
    let pages = []
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i)
    }
    let portionSize = Math.ceil(pagesCount / 10)
    let [portionNumber, setPortionNumber] = useState(1)
    let leftNum = (portionNumber - 1) * 10 + 1
    let rightNum = portionNumber * 10
    let changePage1 = () => {
        page += 20

        // props.changePage()
        setPortionNumber(portionNumber + 1)
        props.setCurrentPage(page)
    }
    let changePage2 = () => {
        page -= 20
        // props.changePagePrev()
        setPortionNumber(portionNumber - 1)
        props.setCurrentPage(page)
    }
    let handlePageChange = (pageNumber) => {
        props.setCurrentPage(pageNumber)

    }

    console.log(portionNumber)
    console.log(leftNum )
    console.log(rightNum )
    console.log(pages.length)

    return <div>
        <div className={s.pageNumbers}>
            {/*<Pagination*/}
            {/*    activePage={props.currentPage}*/}
            {/*    itemsCountPerPage={20}*/}
            {/*    totalItemsCount={props.totalUsersCount}*/}
            {/*    pageRangeDisplayed={4}*/}
            {/*    onChange={handlePageChange}*/}

            {/*    // activeClassName={s.active}*/}
            {/*    activeLinkClass={s.active}*/}
            {/*/>*/}
            {leftNum > portionNumber ? <button onClick={changePage2}>Prev</button> : ''}

            {pages.filter(p => p >= leftNum && p <= rightNum).map(p => {
                return <span onClick={() => {
                    props.setCurrentPage(p)
                }} className={props.currentPage === p ? s.activePage : leftNum === p ? s.activePage : ''}>{p}</span>
            })}

            {rightNum < pages.length  ?  <button onClick={changePage1}>Next</button> :''}

            {/*<div>*/}
            {/*   */}

            {/*    <span>{props.currentPage}</span>*/}
            {/*    <span>{pages.length}</span>*/}
            {/*   */}
            {/*</div>*/}
        </div>
        <div className={s.usersWrapper}>

            {
                props.users.map((el, i) =>
                    <div>
                        <div className={s.left}>
                            <span>{el.id}</span>
                            <div>
                                <NavLink to={'/profile/' + el.id}>
                                    <img
                                        src={el.photos.small !== null ? el.photos.small : 'https://www.kodefork.com/static/users/images/user.png'}
                                        alt=""/>
                                </NavLink>
                            </div>
                            <div>
                                {el.followed
                                    ? <button disabled={props.followingInProgress.some(id => id == el.id)}
                                              onClick={() => {
                                                  props.unfollowing(el.id)
                                              }}>unfollow</button>
                                    : <button disabled={props.followingInProgress.some(id => id == el.id)}
                                              onClick={() => {
                                                  props.following(el.id)
                                              }}>follow</button>}

                            </div>
                        </div>
                        <div className={s.right}>
                            <div>
                                <div>{el.name}</div>
                                <div>{el.status}</div>
                            </div>
                            <div>
                                <div>{"el.locations.city"}</div>
                                <div>{"el.locations.country"}</div>
                            </div>


                        </div>
                    </div>
                )
            }
            <button onClick={() => {
                props.loadUsers(page, flag)
            }}>Load more
            </button>
        </div>
    </div>


}

export default Users
