import {NavLink} from "react-router-dom";
import s from "./Users.module.css"

const Users = (props) => {
    console.log(props)

    if(props.users.length === 0){
        a
        props.setUsers(
            [
                {
                    followed: false,
                    id: '1',
                    photo:'https://upload.wikimedia.org/wikipedia/commons/thumb/8/88/Dmitry_Nagiev_2017_4.jpg/274px-Dmitry_Nagiev_2017_4.jpg',
                    fullName: 'Dmitriy',
                    status: 'jjdsjfsdfjsfs',
                    locations: {
                        country: 'Moscow',
                        city: 'Russia'
                    }

                },
                {
                followed: false,
                photo:'https://upload.wikimedia.org/wikipedia/commons/thumb/8/88/Dmitry_Nagiev_2017_4.jpg/274px-Dmitry_Nagiev_2017_4.jpg',
                id: '2',
                fullName: 'Dmitriy',
                status: 'jjdsjfsdfjsfs',
                locations: {
                    country: 'Moscow',
                    city: 'Russia'
                }

            },
                {
                followed: true,
                id: '3',
                photo:'https://upload.wikimedia.org/wikipedia/commons/thumb/8/88/Dmitry_Nagiev_2017_4.jpg/274px-Dmitry_Nagiev_2017_4.jpg',
                fullName: 'Dmitriy',
                status: 'jjdsjfsdfjsfs',
                locations: {
                    country: 'Moscow',
                    city: 'Russia'
                }

            },
                {
                followed: true,
                id: '4',
                photo:'https://upload.wikimedia.org/wikipedia/commons/thumb/8/88/Dmitry_Nagiev_2017_4.jpg/274px-Dmitry_Nagiev_2017_4.jpg',
                fullName: 'Dmitriy',
                status: 'jjdsjfsdfjsfs',
                locations: {
                    country: 'Moscow',
                    city: 'Russia'
                }

            },
                {
                followed: false,
                id: '5',
                photo:'https://upload.wikimedia.org/wikipedia/commons/thumb/8/88/Dmitry_Nagiev_2017_4.jpg/274px-Dmitry_Nagiev_2017_4.jpg',
                fullName: 'Dmitriy',
                status: 'jjdsjfsdfjsfs',
                locations: {
                    country: 'Moscow',
                    city: 'Russia'
                }

            },

            ]
        )
    }
    debugger

    return <div className={s.usersWrapper}>
        {


            props.users.map((el) =>
                <div>

                    <div className={s.left}>
                        <div>
                            <img src={el.photo} alt=""/>
                        </div>
                      <div>
                          {el.followed
                              ?<button onClick={()=>  {props.unfollow(el.id)}}>unfollow</button>
                              : <button onClick={()=>{props.follow(el.id)}}>follow</button>}
                      </div>



                    </div>
                    <div className={s.right}>
                        <div>
                            <div>{el.fullName}</div>
                            <div>{el.status}</div>
                        </div>
                        <div>
                            <div>{el.locations.city}</div>
                            <div>{el.locations.country}</div>
                        </div>


                    </div>
                </div>
            )
        }
    </div>
}
export default Users
