const addnews = 'addnews'
let initialState =
    {
        allNews: [
            {

                data: '23.10.2020',
                news: 'la la la la ',
                author: 'Katy',
                newsState: false
            },
            {

                data: '14.05.2020',
                news: 'pa pa pa pa  ',
                author: 'Ann',
                newsState: false
            },
            {
           
                data: '18.09.2020',
                news: 'yo yo yo yo yo ',
                author: 'Jack',
                newsState: false
            }
        ]
    }


const newsReducer = (state = initialState, action) => {
    switch (action.type) {
       case addnews:

            let newArr = state.allNews.splice(action.id, 1)

            return {
                ...state,
               allNews:[...state.allNews, newArr]

            }


        default :
            return state
    }
}

export default newsReducer
export const addNewsActionCreator = (id) => {

    return {
        type: addnews,
        id: id
    }
};

