import React from "react";

import {NavLink} from "react-router-dom";
import s from "./Users.module.css"
import * as axios from "axios";


let Users = (props) => {
console.log(props)
        let page;
        let flag= true;
        let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize);
        let pages = []
        for (let i = 1; i <= pagesCount; i++) {
            pages.push(i)
        }

        return <div>
            <div className={s.pageNumbers}>
                {pages.map(p => {
                    return <span onClick={() => {

                        props.setCurrentPage(p)
                    }} className={props.currentPage === p && s.activePage}>{p}</span>
                })}
            </div>
            <div className={s.usersWrapper}>

                {
                  props.users.map((el, i) =>
                        <div>
                            <div className={s.left}>
                                <span>{el.id}</span>
                                <div>
                                    <NavLink to={'/profile/' + el.id}>
                                    <img
                                        src={el.photos.small !== null ? el.photos.small : 'https://www.kodefork.com/static/users/images/user.png'}
                                        alt=""/>
                                    </NavLink>
                                </div>
                                <div>
                                    {el.followed
                                        ? <button onClick={() => {
                                            axios.post(`https://social-network.samuraijs.com/api/1.0/users?page=${el.id}`, {},  {withCredentials: true}).then(response => {
                                                props.follow(el.id)
                                            })
                                        }}>unfollow</button>
                                        : <button onClick={() => {
                                            axios.post(`https://social-network.samuraijs.com/api/1.0/users?page=${el.id}`, {},  {withCredentials: true}).then(response => {
                                                if(response.data.resultCode)
                                                props.follow(el.id)
                                            })



                                        }}>follow</button>}
                                </div>
                            </div>
                            <div className={s.right}>
                                <div>
                                    <div>{el.name}</div>
                                    <div>{el.status}</div>
                                </div>
                                <div>
                                    <div>{"el.locations.city"}</div>
                                    <div>{"el.locations.country"}</div>
                                </div>


                            </div>
                        </div>
                    )
                }
                <button onClick={() => {
                    props.loadUsers(page, flag)
                }}>Load more
                </button>
            </div>
        </div>



}

export default Users
