import React from "react";


class StatusProfile extends React.Component {
    constructor(props) {
        super(props);
    }

    state = {
        editMode: false
    }

    setState(state) {
        return {editMode: true}

    }

    render() {
        return (
            <div>
                {!this.state.editMode &&
                <span onDoubleClick={this.setState}></span>
                }
                {this.state.editMode &&
                < input type="text" value={this.props.status}/>
                }
            </div>
        )
    }


}

export default StatusProfile
