import React from 'react';
import {NavLink} from "react-router-dom";


const OneNews = (props) => {
    console.log(props[0])

    return (

        <div className='news-item'>
            <span class='data'>{props[0].data}</span>
            {/*<span class='news-text'>{props[0].news}</span>*/}
            {/*<span class='name'>{props[0].name}</span>*/}
        </div>

    )
}

const News = (props) => {


    let addNews = () => {
        console.log(props)
        OneNews(props)
    }
    return (
        <div>

            <button onClick={addNews}>ADD NEWS</button>
            <div className="news-container">
                <OneNews data={props.news} />
            </div>
        </div>
    )
}


export default News
