                users: state.users.map((el)=>{
                    if(el.id === action.id){
                        return {...el, followed: false }
                    }
                    return el
                })
            }
        case SET_USERS:
            return {

              ...state , users:[...state.users, ...action.users]
            }
        default:
            return state
    }
}

export default usersReducer
export const followedAC = (id) => {

    return {
        type: FOLLOWED,
        id: id
    }
};
export const unfollowedAC = (id) => {

    return {
        type: UNFOLLOWED,
        id: id
    }
};
export const setUsersAC = (users) => {

    return {
        type: SET_USERS,
