    warn
})(LoginForm)

let Login = (props) => {

    let loginSubmit = (formData) => {
        formData = {}
        console.log(formData)

    }

    return <div>
        <h1>Login</h1>
        <ReduxLoginForm onSubmit={loginSubmit}/>
