import React from "react";


class StatusProfile extends React.Component {
    constructor(props) {
        super(props);
    }
    state={
        editMode:false
    }
setState(state) {
    editMode: true
}

    render() {
        return (
            <div>
                {!this.state.editMode &&
                <span  onDoubleClick={()=>{alert('kkkk')}}>{this.props.status}</span>
                }
                {this.state.editMode &&
                < input type="text" value={this.props.status}/>
                }
            </div>
        )
    }


}

export default StatusProfile
