import React from "react";

import {NavLink} from "react-router-dom";
import s from "./Users.module.css"
import * as axios from "axios";
import {setCurrentPageAC} from "../../redux/users-reducer";

class Users extends React.Component {

    constructor(props) {
        super(props);

    }

    componentDidMount() {

        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`).then(response => {
            console.log(response.data.items)
            this.props.setUsers(response.data.items)
        })
        console.log(this.props)
    }

    loadUsers(page, count, razepage) {
        page += 1;
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${page}&count=${count}`).then(response => {
            this.props.setUsers(response.data.items)
            console.log(this.props.users)
        })
    }
    setCurrentPage(page){
        this.props.setCurrentPage(page)

        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${page}&count=${this.props.pageSize}`).then(response => {
            console.log(response.data.items)

            this.props.setUsers(response.data.items)
            this.props.setTotalUsersCount(response.data.items)
        })

    }

    render() {
        let page = 1;
        let count = 2;
        let rasepage;
        let pagesCount = Math.ceil(this.props.totalUsersCount / this.props.pageSize);
        let pages = []
        for (let i = 1; i <= pagesCount; i++) {
            pages.push(i)
        }

        return <div>
            <div className={s.pageNumbers}>
                {pages.map(p => {
                  return  <span onClick={()=>{this.setCurrentPage(p)}} className={this.props.currentPage === p && s.activePage}>{p}</span>
                })}
            </div>
            <div className={s.usersWrapper}>

                {


                    this.props.users.map((el) =>
                        <div>

                            <div className={s.left}>
                                <div>
                                    <img
                                        src={el.photos.small !== null ? el.photos.small : 'https://upload.wikimedia.org/wikipedia/commons/thumb/8/88/Dmitry_Nagiev_2017_4.jpg/274px-Dmitry_Nagiev_2017_4.jpg'}
                                        alt=""/>
                                </div>
                                <div>
                                    {el.followed
                                        ? <button onClick={() => {
                                            this.props.unfollow(el.id)
                                        }}>unfollow</button>
                                        : <button onClick={() => {
                                            this.props.follow(el.id)
                                        }}>follow</button>}
                                </div>


                            </div>
                            <div className={s.right}>
                                <div>
                                    <div>{el.name}</div>
                                    <div>{el.status}</div>
                                </div>
                                <div>
                                    <div>{"el.locations.city"}</div>
                                    <div>{"el.locations.country"}</div>
                                </div>


                            </div>

                        </div>
                    )
                }
                <button onClick={() => {
                    this.loadUsers(page, count, rasepage)
                }}>Load more
                </button>
            </div>
        </div>


    }
}

export default Users
