import React from 'react';
import s from './Dialogs.module.css';
import {NavLink} from "react-router-dom";



const DialogItem = (props) => {

    console.log(props)
    let path = '/dialogs/' + props.id;
    return (
        <NavLink to={path} className={s.dialog} activeClassName={s.active}>{props.name}</NavLink>
    )
}

const Message = (props) => {
    return (
        <div className="message">{props.message}</div>
    )
}
const Dialogs = (props) => {
    debugger
    let newMessageElement = React.createRef()
    const addMessage = () => {
        props.addMessage()
    }
    let onMessageChange = () => {

        let text = newMessageElement.current.value;
        console.log(text)
        props.onMessageChange(text)
    }
    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItem}>
                {props.dialogs.map((el, i) =>
                    <DialogItem name={el.name} id={el.id}/>
                )}
            </div>
            <div>
                {props.message.map((el, i) =>
                    <Message message={el.message}/>
                )}
            </div>
            <div>
                <textarea onChange={onMessageChange} ref={newMessageElement} value={props.textMessage}/>
                <button onClick={addMessage}>Add Message</button>
            </div>
        </div>)
}


export default Dialogs
