import React from "react";


class StatusProfile extends React.Component {
    constructor(props) {
        super(props);
        console.log(this.state.status)
    }
    componentDidUpdate = (prevProps)=>{
        this.setState(
            {
                status: this.props.status
            }
        )
    }
    state = {
        editMode: false,
        status: this.props.status
    }

    activateMode = () => {
        this.setState(
            {
                editMode: true
            }
        )
    }
    deActivateMode = () => {
        this.setState(
            {
                editMode: false
            }
        )
        this.props.updateStatus(this.state.status)
    }
    updateStatus = (e) => {

        this.setState(
            {
                status: e.currentTarget.value
            }
        )

    }

    render() {
        return (
            <div>
                {!this.state.editMode &&
                <span onDoubleClick={this.activateMode}>{this.props.status|| 'here can be your status...'}</span>
                }
                {this.state.editMode &&
                < input onChange={this.updateStatus} autoFocus={true} onBlur={this.deActivateMode} type="text" value={this.state.status}/>
                }
            </div>
        )
    }


}

export default StatusProfile
