import React, {useState} from "react";


const StatusProfile = () => {
    let [editMode, setState] = useState(false)
    // componentDidUpdate = (prevProps, prevState)=>{
    //     if(prevProps.status !== this.props.status){
    //         this.setState(
    //             {
    //                 status: this.props.status
    //             }
    //         )
    //     }
    //     console.log(this.state.status)
    // }
    // state = {
    //     editMode: false,
    //     status: this.props.status
    // }

    let activateMode = () => {
        setState(
            {
                editMode: true
            }
        )
    }
    let deActivateMode = () => {
        setState(
            {
                editMode: false
            }
        )
        this.props.updateStatus(this.state.status)
    }
    let updateStatus = (e) => {

        setState(
            {
                status: e.currentTarget.value
            }
        )

    }

    return <div>
        {!editMode &&
        <span onDoubleClick={activateMode}>{this.props.status || 'here can be your status...'}</span>
        }
        {editMode &&
        < input onChange={updateStatus} autoFocus={true} onBlur={deActivateMode} type="text" />
        }
    </div>


}

export default StatusProfile
