import React from "react";

import {connect} from "react-redux";

import {followedAC, setCurrentPageAC, setTotalUsersCountAC, setUsersAC, unfollowedAC} from "../../redux/users-reducer";
import preloader from "./../../images/loader.jpg"

import * as axios from "axios";
import Users from "./Users";

class UsersContainer extends React.Component {

    constructor(props) {
        super(props);
        console.log(props)
    }

    componentDidMount() {

        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`).then(response => {
            console.log(response.data.items)
            this.props.setUsers(response.data.items)
            this.props.setTotalUsersCount(response.data.totalCount)
        })
        console.log(this.props)
    }

    loadUsers = (page, flag) => {
        page = this.props.currentPage;
        page += 1;
        this.props.setCurrentPage(page);
        console.log(page);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${page}&count=${this.props.pageSize}`).then(response => {
            this.props.setUsers(response.data.items, flag);
        })
    }

    setCurrentPage = (page) => {

        this.props.setCurrentPage(page);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${page}&count=${this.props.pageSize}`).then(response => {
            this.props.setUsers(response.data.items)
        })

    }

    render() {
        return <>
            {this.props.isFetching ? <img src={preloader} alt=""/> : null}
            <Users totalUsersCount={this.props.totalUsersCount}
                   pageSize={this.props.pageSize}
                   users={this.props.users}
                   setCurrentPage={this.setCurrentPage}
                   unfollow={this.props.unfollow}
                   follow={this.props.follow}
                   loadUsers={this.loadUsers}
                   currentPage={this.props.currentPage}/>
        </>
    }
}

let mapStateToProps = (state) => {

    return {
        users: state.usersPage.users,
        pageSize: state.usersPage.pageSize,
        totalUsersCount: state.usersPage.totalUsersCount,
        currentPage: state.usersPage.currentPage,
        isFetching: state.usersPage.isFetching,
    };
};
let mapDispatchToProps = (dispatch) => {
    return {
        follow: (id) => {
            dispatch(followedAC(id))
        },
        unfollow: (id) => {
            dispatch(unfollowedAC(id))
        },
        setUsers: (users, flag) => {
            dispatch(setUsersAC(users, flag))
        },
        setTotalUsersCount: (count) => {
            dispatch(setTotalUsersCountAC(count))
        },
        setCurrentPage: (page) => {
            dispatch(setCurrentPageAC(page))
        }

    }
}

export default connect(mapStateToProps, mapDispatchToProps)(UsersContainer)
