import React from "react";

import {NavLink} from "react-router-dom";
import s from "./Users.module.css"
import * as axios from "axios";

class Users extends React.Component {

    constructor(props) {
        super(props);

    }

    componentDidMount() {

        axios.get('https://social-network.samuraijs.com/api/1.0/users?page=1&count=3').then(response => {
            this.props.setUsers(response.data.items)
        })

    }


    render() {
        let page = 1;
        let count = 3
        let loadUsers= ( page, count) =>{
            debugger
            page++;

            count = count+3
            axios.get('https://social-network.samuraijs.com/api/1.0/users?page=page&count=count').then(response => {
                this.props.setUsers(response.data.items)
            })
        }
        return <div className={s.usersWrapper}>
            {


                this.props.users.map((el) =>
                    <div>

                        <div className={s.left}>
                            <div>
                                <img
                                    src={el.photos.small !== null ? el.photos.smal : 'https://upload.wikimedia.org/wikipedia/commons/thumb/8/88/Dmitry_Nagiev_2017_4.jpg/274px-Dmitry_Nagiev_2017_4.jpg'}
                                    alt=""/>
                            </div>
                            <div>
                                {el.followed
                                    ? <button onClick={() => {
                                        this.props.unfollow(el.id)
                                    }}>unfollow</button>
                                    : <button onClick={() => {
                                        this.props.follow(el.id)
                                    }}>follow</button>}
                            </div>


                        </div>
                        <div className={s.right}>
                            <div>
                                <div>{el.name}</div>
                                <div>{el.status}</div>
                            </div>
                            <div>
                                <div>{"el.locations.city"}</div>
                                <div>{"el.locations.country"}</div>
                            </div>


                        </div>

                    </div>
                )
            }
            <button onClick={loadUsers}>Load more</button>
        </div>

    }
}

export default Users
