   let newPostElement = React.createRef()
    const addPost = () =>{
       let  text = newPostElement.current.value
        alert(text)
  }
    return <div>
        <div>
            <h3> My post</h3>
            <textarea></textarea>
            <button ref={newPostElement} onClick={addPost }>add button</button>
            <button>remove button</button>
        </div>
        <div>
            {arr_post}
        </div>
    </div>
}
export default MyPosts
