import React, {useState} from "react";
import s from './ProfileInfo.module.css';
import Preloader from "../../common/preloader/Preloader";
import StatusProfile from "./StatusProfile/StatusProfile";
import {Field, reset, reduxForm} from 'redux-form'
import {AxiosInstance as axios} from "axios";
import {instance} from "../../../api/api"
import {unfollow} from "../../../redux/users-reducer";

const addFileForm = (props) => {
    const {handleSubmit, load, pristine, reset, submitting} = props
    const {createRecord, resetForm} = props;


    return (

        <form onSubmit={props.handleSubmit}>


            <div>
                <Field name='filname' component='input' type="file" value={'nnnn'}/>
            </div>


            <button>Add photo</button>
        </form>
    )

}
const ReduxFileForm = reduxForm({
    // a unique name for the form
    form: 'file',

})(addFileForm)
const ProfileInfo = (props) => {
    console.log(props)

    if (!props.profile) {
        return <Preloader/>
    }

    // eslint-disable-next-line react-hooks/rules-of-hooks
    let [file, setState] = useState(null)
    // eslint-disable-next-line react-hooks/rules-of-hooks
    let [img, setStateUrl] = useState(null)
    let  onFormSubmit = (e) => {

        e.preventDefault();
        const formData = new FormData();

        formData.append('image', file);
        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            }
        };
        debugger
        instance.put('/profile/photo', formData, config).then((response) => {
            if (response.data.resultCode == 0) {
                console.log(response.data)
            }
            console.log(response.data)
        })
//         let reader = new FileReader();
// console.log(reader)
//         let file2 = e.target.files[0];
//
//         reader.onloadend = () => {
//             setState(file2)
//             setStateUrl(reader.result)
//
//         }
//         reader.readAsDataURL(file2)
//
//         ProfileAPI.photo('6880', file2).then((response) => {
//             if (response.data.resultCode == 0) {
//                 console.log(response)
//             }
//             })
    }
    let onChange = (e) => {
        setState(e.target.files[0]);

    }
    return (
        <div>
            {/*<div><img src='data:image/jpeg;base64,' alt=""/>*/}
            {/*</div>*/}
            <div className={s.avatar}>
                <img
                    src={props.profile.photos.large !== null ? props.profile.photos.large : "https://www.kodefork.com/static/users/images/user.png"}
                    alt=""/>

            </div>
            <form onSubmit={onFormSubmit}>
                <input type="file" name="myImage" onChange={onChange}/>
                <button type="submit">Upload</button>
            </form>
            <img src={file}/>
            {/*<ReduxFileForm onSubmit={submitFileForm}/>*/}
            <StatusProfile status={props.status} updateStatus={props.updateStatus}/>
        </div>
    )
}

export default ProfileInfo
