import React from "react";


class StatusProfile extends React.Component {
    constructor(props) {
        super(props);
    }

    state = {
        editMode: false
    }
    activateMode = () => {
        this.setState(
            {
                editMode: true
            }
        )
    }


    render() {
        return (
            <div>
                {!this.state.editMode &&
                <span onDoubleClick={this.activateMode}>{this.props.status}</span>
                }
                {this.state.editMode &&
                < input type="text" value={this.props.status}/>
                }
            </div>
        )
    }


}

export default StatusProfile
