let updateNewMessagePost = 'updateNewMessagePost';
let addMessage = 'ADD-Message';

let initialState =  {
    messagesList: [
        {
            id: 1,
            message: 'Hi'
        },
        {
            id: 2,
            message: 'How are you?'
        },
        {
            id: 3,
            message: 'How is yore it?'
        },
        {
            id: 4,
            message: 'yo!!!!'
        }
    ],
    names: [
        {
            name: 'Dimych',
            id: '1'
        }, {
            name: 'Kate',
            id: '2'
        }, {
            name: 'Ann',
            id: '3'
        }, {
            name: 'Sveta',
            id: '4'
        }, {
            name: 'Andru',
            id: '5'
        },
    ],
    textMessage: 'lalala'
}
const messageReducer = (state = initialState, action) => {
    switch (action.type) {
        case addMessage:
            let newMessage = {
                id: action.id,
                message: state.textMessage
            };
            return {
                ...state,
                messagesList:  [...state.messagesList, newMessage],
                textMessage: ''
            }
    // state.messagesList.push(newMessage);
    // state.
    // return state
        case updateNewMessagePost:
            return {
                ...state,
                textMessage:action.nextMessage
            }
            // state.textMessage = action.nextMessage;
            // return state;
        default :
            return state
    }


}
export const addMessageActionCreator = (id) => {

    return {
        type: addMessage,

        id: id
    }
};
export const UpdateMessagePostActionCreator = (text, id) => {

    return {
        type: updateNewMessagePost,
        nextMessage: text
    }
};
export default messageReducer
