import React, {useEffect, useState} from "react";


const StatusProfile = (props) => {
    let [editMode, setState] = useState(false)
    // componentDidUpdate = (prevProps, prevState)=>{
    //     if(prevProps.status !== this.props.status){
    //         this.setState(
    //             {
    //                 status: this.props.status
    //             }
    //         )
    //     }
    //     console.log(this.state.status)
    // }
    useEffect(()=>{
        setState(
            {
                status: props.status
            }
        )
    })
 let   state = {
        editMode: false,
        status: props.status
    }

    let activateMode = () => {
        setState(
            {
                editMode: true
            }
        )
    }
    let deActivateMode = () => {
        setState(
            {
                editMode: false
            }
        )
    props.updateStatus(state.status)
    }
    let updateStatus = (e) => {

        setState(
            {
                status: e.currentTarget.value
            }
        )

    }

    return <div>
        {!editMode &&
        <span onDoubleClick={activateMode}>{props.status || 'here can be your status...'}</span>
        }
        {editMode &&
        < input onChange={updateStatus} autoFocus={true} onBlur={deActivateMode} type="text"/>
        }
    </div>


}

export default StatusProfile
