import React from 'react'
import s from './FormControlsModule.css'
export const TextArea = ({input, meta, ...props}) => {


    return (
        <div className={s.formControls}>

            <div>
                <textarea {...input} {...props} />
            </div>
            <span>{'some error'}</span>

        </div>

    )
}
