import React, {useState} from "react";
import s from './ProfileInfo.module.css';
import Preloader from "../../common/preloader/Preloader";
import StatusProfile from "./StatusProfile/StatusProfile";
import {Field, reset, reduxForm} from 'redux-form'
import {AxiosInstance as axios} from "axios";
import {instance, ProfileAPI} from "../../../api/api"
import {unfollow} from "../../../redux/users-reducer";
import Profile from "../Profile";

const addFileForm = (props) => {
    const {handleSubmit, load, pristine, reset, submitting} = props
    const {createRecord, resetForm} = props;


    return (

        <form onSubmit={props.handleSubmit}>


            <div>
                <Field name='filname' component='input' type="file" value={'nnnn'}/>
            </div>


            <button>Add photo</button>
        </form>
    )

}
const ReduxFileForm = reduxForm({
    // a unique name for the form
    form: 'file',

})(addFileForm)
const ProfileInfo = (props) => {
    console.log(props)

    if (!props.profile) {
        return <Preloader/>
    }

    // eslint-disable-next-line react-hooks/rules-of-hooks
    let onChangeFile = (e) => {
        e.preventDefault();

        if(e.target.files.length) {
            props.setPhotoApi(e.target.files[0])
        }
    }

    return (
        <div>

            <div className={s.avatar}>
                {/*<img*/}
                {/*    src={props.profile.photos.small || props.photo !== null ? props.profile.photos.small : "https://www.kodefork.com/static/users/images/user.png"}*/}
                {/*    alt=""/> */}
                <img
                    src={props.profile.photos.large || props.photo}
                    alt=""/>

            </div>

            {props.ownerId &&


            <div>
                <input type="file" name="myImage" onChange={onChangeFile}/>
            </div>
            }

            {/*<ReduxFileForm onSubmit={submitFileForm}/>*/}
            <StatusProfile status={props.status} updateStatus={props.updateStatus}/>
            <div>
                <div><b>Full name:</b>{props.profile.fullname}</div>
                <div></div>
            </div>
        </div>
    )
}

export default ProfileInfo
