import React from "react";
import s from './MyPosts.module.css'
import MyPost from './MyPost/MyPost'
// import {updateNewTekstPost} from "../../../redux/state";
// import App from "../../../App";

const MyPosts = (props) => {
    console.log(props)
    let arr_post = props.posts.map((el) => <MyPost massage={el.massage} like={el.like}/>);
    let newPostElement = React.createRef()
    const addPost = () => {
        let action = {type:'ADD-POST'};
        props.dispatch(action)
    }
    const onPostChange = () => {

        let text = newPostElement.current.value;
        let action = {type:'updateNewTextPost', nextText:text};
        props.dispatch(action )

    }
    return <div>
        <div>
            <h3> My post</h3>
            <textarea onChange={onPostChange} ref={newPostElement} value={props.newPostText}/>
            <button onClick={addPost}>add button</button>
            <button>remove button</button>
        </div>
        <div>
            {arr_post}
        </div>
    </div>
}
export default MyPosts
