    //     axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${page}&count=${this.props.pageSize}`).then(response => {
    //         this.props.setUsers(response.data.items)
    //     })
    //
    // }


        let page;
        let flag= true;
        let pagesCount = Math.ceil(this.props.totalUsersCount / this.props.pageSize);
        let pages = []
        for (let i = 1; i <= pagesCount; i++) {
            pages.push(i)
        }

        return <div>
            <div className={s.pageNumbers}>
                {pages.map(p => {
                    return <span onClick={() => {
                        props.setCurrentPage(p)
                    }} className={props.currentPage === p && s.activePage}>{p}</span>
                })}
            </div>
            <div className={s.usersWrapper}>

                {
                  props.users.map((el, i) =>
                        <div>
                            <div className={s.left}>
                                <span>{el.id}</span>
                                <div>
                                    <img
                                        src={el.photos.small !== null ? el.photos.small : 'https://upload.wikimedia.org/wikipedia/commons/thumb/8/88/Dmitry_Nagiev_2017_4.jpg/274px-Dmitry_Nagiev_2017_4.jpg'}
                                        alt=""/>
                                </div>
                                <div>
                                    {el.followed
                                        ? <button onClick={() => {
                                          props.unfollow(el.id)
                                        }}>unfollow</button>
                                        : <button onClick={() => {
                                           props.follow(el.id)
                                        }}>follow</button>}
                                </div>
                            </div>
                            <div className={s.right}>
                                <div>
                                    <div>{el.name}</div>
                                    <div>{el.status}</div>
                                </div>
                                <div>
                                    <div>{"el.locations.city"}</div>
                                    <div>{"el.locations.country"}</div>
                                </div>


                            </div>
                        </div>
                    )
                }
                <button onClick={() => {
                    props.loadUsers(page, flag)
                }}>Load more
                </button>
            </div>
        </div>



}

export default Users
