import React from "react";


class StatusProfile extends React.Component {
    constructor(props) {
        super(props);
    }

    state = {
        editMode: false,
        state: this.props.status
    }
    activateMode = () => {
        this.setState(
            {
                editMode: true
            }
        )
    }
    deActivateMode = () => {
        this.setState(
            {
                editMode: false
            }
        )
    }
    updateStatus = (e) => {
        console.log(e)
        // this.props.updateStatus()
    }

    render() {
        return (
            <div>
                {!this.state.editMode &&
                <span onDoubleClick={this.activateMode}>{this.props.status}</span>
                }
                {this.state.editMode &&
                < input onChange={this.updateStatus} autoFocus={true} onBlur={this.deActivateMode} type="text" value={this.props.status}/>
                }
            </div>
        )
    }


}

export default StatusProfile
