import React from 'react';

import {addMessageActionCreator, UpdateMessagePostActionCreator} from "../../redux/message-reducer";
import Dialogs from "./Dialogs";
import {connect} from "react-redux";
import {withAuthRedirect} from "../../hoc/AuthRedirect";


let mapStateToProps = (state) => {
    return {
        dialogs: state.messagePage.names,
        textMessage: state.messagePage.textMessage,
        message: state.messagePage.messagesList,
    };
};
let mapDispatchToProps = (dispatch) => {
    return {
        onMessageChange: (text) => {
            dispatch(UpdateMessagePostActionCreator(text))
        },
        addMessage: () => {
        dispatch(addMessageActionCreator())
        }
    }
}
let mapStateToPropsRedirect = (state) => {
    return {
        isAuth: state.auth.isAuth
    };
};
let AuthRedirectComponent = withAuthRedirect(Dialogs)
AuthRedirectComponent =connect(mapStateToPropsRedirect)(AuthRedirectComponent)
const DialogsContainer = connect(mapStateToProps, mapDispatchToProps)(AuthRedirectComponent)

export default DialogsContainer
