import React from "react";
import {connect} from "react-redux";
import {Field, reduxForm} from 'redux-form'

import {Input} from "../common/FormControls/FormControls";
import {maxLength10, required} from "../../utils/validator/validator";


const LoginForm = (props) => {

    return (

        <form onSubmit={props.handleSubmit}>


            <div>
                <Field  placeholder={'login'} name={'login'} validate={[required]} component={Input}/>
            </div>
            <div>
                < Field validate={[required]} placeholder={'passwоrd'} name={'password'} component={Input}/>
            </div>
            <div>
                <Field   type={'checkbox'} name={'rememberMe'} component={Input}/>
            </div>


            <button>Login</button>
        </form>
    )

}




let Login = (props) => {

    let loginSubmit = (formData) => {
        formData = {}
        console.log(formData)

    }

    return <div>
        <h1>Login</h1>
        <ReduxLoginForm onSubmit={loginSubmit}/>
    </div>


}


// You have to connect() to any reducers that you wish to connect to yourself

export default connect()(Login)
