import React from "react";
import {CreateInput, InputData, Input } from "../../common/FormControls/FormControls";
import {connect} from "react-redux";
import { reduxForm,Field} from 'redux-form'

import {required} from "../../../utils/validator/validator";

const ProfileDataForm = (props) => {
    return <form>
        {props.ownerId &&  <button onClick={()=>{}}>Save</button>}
        <div>{CreateInput('ff', 'name', [], Input, {type: 'text', label: 'Full Name'} )}</div>
        {/*<Field  placeholder={'Name'} name={'name'} validate={[required]} component={InputData} label='Full Name' type='text'/>*/}
        {/*<Field  placeholder={'Email'} name={'email'} validate={[required]} component={InputData} label='Email' type='email'/>*/}
        <div><b>Full name:</b>{props.profile.fullname}</div>
        <div><b>Looking for a job:</b>{props.profile.lookingForAJob ? 'yes' : 'no'} </div>
        {props.profile.lookingForAJob &&
        <div><b>My profetional skils:</b> {props.profile.lookingForADescription}</div>
        }
        <div><b>About me:</b> {props.profile.aboutMe}</div>

        {/*<div><b>Contact:</b>*/}
        {/*    {Object.keys(props.profile.contacts).map(key => {*/}
        {/*        return <Contact contactsTitle={key} contactsValue={props.profile.contacts[key]}/>*/}
        {/*    })}*/}

        {/*</div>*/}


    </form>
}
const ReduxDataForm = reduxForm({
    // a unique name for the form
    form: 'dataForm',

})(ProfileDataForm)


export default  ReduxDataForm
