import {required} from "../../../utils/validator/validator";

const ProfileDataForm = (props) => {
    return <form>
        {props.ownerId &&  <button onClick={()=>{}} disabled={pristine || submitting}>Save</button>}
        <div>{CreateInput('', 'name', [required], Input, {type: 'text', label: 'Full Name:'} )}</div>
        <div>{CreateInput('', 'checkbox', [], Input, {type: 'checkbox', label: 'Looking for a job:'} )}</div>
        {/*{props.profile.lookingForAJob &&*/}
        {/*}*/}
